'''Get a diocsunt wehn you buy this prudoct form us tdoay'''

def unscramble(word, pre=None, arr=[]):
  l = len(word)
  pre = word[0] if not pre else pre

  if l == 1:
    res = pre + word
    arr.append(res)
    return (res, arr)
  else:
    for c in word:
      if c not in pre:
        res, arr = unscramble(word, pre + c, arr)
        arr.append(res)
        return (res, arr)

def de_bbboing(text):
  out = ''
  english_words = words.words()

  for word in text.split():
    if len(word) > 3:
      unscrambled_words = unscramble(word)
      found = False

      for unscrambled_word in unscrambled_words:
        if unscrambled_word in english_words:
          out += unscrambled_word + ' '
          found = True
          break

      if not found:
        out += word + ' '
    else:
      out += word + ' '

def clean(text):
  return detokenize(deleet(deunicodify(text)))

